import styles from './Login.module.css'
import TextBox from '../TextBox/TextBox'
import { useFormik } from 'formik';
import {useNavigate } from 'react-router-dom'
import LoginSchema from '../Schemas/LoginSchema';
function Login()
{
    const navigate = useNavigate();
    const HandleLogin = () => {
        navigate('/')
    }
    const {values, touched, handleBlur, handleChange, errors} = useFormik({
        initialValues: {
            email:"",
            password:"",

        },
        validationSchema: LoginSchema 
    })
    return(
        <div className={styles.mainPage}>
        <div className={styles.pageHeader}>
            Login to Your Accout
        </div>
        <TextBox type="text" placeholder="Enter your Email" name="email" onChange={handleChange} onBlur={handleBlur} value={values.email} />
        <TextBox type="password" placeholder="Enter your Password" name="password" onChange={handleChange} onBlur={handleBlur} value={values.password} />
        <button onClick={HandleLogin} className={styles.LoginButton}>
            Login
        </button>
        </div>
    )
}

export default Login
